import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Apartment from './Apartment1';
import 'bootstrap/dist/css/bootstrap.min.css';

const SearchResults = () => {
  const { q } = useParams();
  const [apartments, setApartments] = useState([]);

  useEffect(() => {
    // Fetch apartments based on the search query
    fetch(`https://www.amadriapark.com?q=${q}`)
      .then(response => response.json())
      .then(data => setApartments(data));
  }, [q]);

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Search Results for {q}</h1>
      <div className="row">
        {apartments.map(apartment => (
          <div key={apartment.id} className="col-md-4 mb-4">
            <Apartment apartment={apartment} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default SearchResults;
