import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const BookingForm = ({ apartment }) => {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [availability, setAvailability] = useState(null);

  const handleBooking = (e) => {
    e.preventDefault();
    // Fetch availability based on the dates
    // For demonstration purposes, assume the API call is successful
    // Replace with actual API call
    const isAvailable = true; // Replace with actual API response
    setAvailability(isAvailable);
  };

  return (
    <form onSubmit={handleBooking}>
      <label>Start Date:</label>
      <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
      <label>End Date:</label>
      <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
      <button type="submit">Check Availability</button>
      {availability !== null && (
        <div>
          <h3>Availability: {availability ? 'Available' : 'Not Available'}</h3>
          {availability && (
            <button type="button" className="btn btn-primary">Book Now</button>
          )}
        </div>
      )}
    </form>
  );
};

export default BookingForm;
