import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Apartment1 = () => {
  const apartment = {
    id: 1,
    title: 'Apartment 1',
    price: 1000,
    location: '123 Main St, Anytown, USA',
    rooms: 2,
    baths: 1,
    image: 'https://images.pexels.com/photos/271624/pexels-photo-271624.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">{apartment.title}</h1>
      <div className="row">
        <div className="col-md-6">
          <img src={apartment.image} alt={apartment.title} style={{ width: '100%', height: '300px', objectFit: 'cover' }} />
        </div>
        <div className="col-md-6">
          <h2>Details</h2>
          <p>Price: {apartment.price}</p>
          <p>Location: {apartment.location}</p>
          <p>Rooms: {apartment.rooms}</p>
          <p>Baths: {apartment.baths}</p>
          <form>
            <label>Start Date:</label>
            <input type="date" />
            <label>End Date:</label>
            <input type="date" />
            <button type="submit">Book Now</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Apartment1;
